import React, { Component } from 'react'
import NewsItem from './NewsItem';
import PropTypes from 'prop-types'
import Spinner from './Spinner';

export class News extends Component {
  constructor(){
    super();
    console.log("hello there i am constructor in the new.js");
    this.state = {
      articles: [],
      loading: false,
      page:1  
    }
  }
  async componentDidMount(){
    console.log("dfdsfdas");
      let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=7b867eedef3745b5a6ecedc47276cfaf&page=${this.state.page}&pagesize=${this.props.pageSize}`;   
      let data = await fetch(url);
      let parsedata = await data.json();
      console.log(parsedata);
      this.setState({articles: parsedata.articles,totalResults:parsedata.totalResults});
  }
    handlePrevious= async()=>{
      let url =
        `https://newsapi.org/v2/top-headlines?country=in&apiKey=7b867eedef3745b5a6ecedc47276cfaf&page=${this.state.page}&pagesize=${this.props.pageSize}`;
      let data = await fetch(url);
      this.setState({
        loading: true
      })
      let parsedata = await data.json();
      this.setState({
        loading:false
      })
      this.setState({
        page: this.state.page - 1,
        articles: parsedata.articles,
      });

    }
    handleNext= async()=>{
      if(!(this.state.page+1>Math.ceil(this.state.totalResults/this.props.pageSize))){
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=7b867eedef3745b5a6ecedc47276cfaf&page=${this.state.page}&pagesize=${this.props.pageSize}`;
        this.setState({
          loading:true
        })
        let data = await fetch(url);
        let parsedata = await data.json();
        this.setState({
          loading: false,
        });
        this.setState({
          page: this.state.page + 1,
          articles: parsedata.articles,
        });
      }
    }

  render() {
    
    return (
      <>
        <div className="container my-3">
          <h1 className='text-center'>News Headlines</h1>
          {this.state.loading && <Spinner/>}
          <div className="row">
            {this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsItem
                    title={element.title ? element.title.slice(0, 50) : ""}
                    description={
                      element.description
                        ? element.description.slice(0, 100)
                        : ""
                    }
                    imageUrl={
                      element.urlToImage
                        ? element.urlToImage
                        : "https://akm-img-a-in.tosshub.com/indiatoday/2023-01/mars%20sample%20return.gif?VersionId=Aav6viGUADS8lx8cuegKfG8ETRHTDrnv"
                    }
                    newsUrl={element.url}
                  />
                </div>
              );
            })}
          </div>
          <div className="conatiner d-flex justify-content-between">
            <button
              disabled={this.state.page <= 1}
              type="button"
              className="btn btn-dark"
              onClick={this.handlePrevious}
            >
              &larr; Previous
            </button>
            <button
              disabled={this.state.page+1>Math.ceil(this.state.totalResults/this.props.pageSize)}
              type="button"
              className="btn btn-dark"
              onClick={this.handleNext}
            >
              Next &rarr;
            </button>
          </div>
        </div>
      </>
    );
  }
}

export default News
