import React, { Component } from 'react'
import NewsItem from './NewsItem';

export class News extends Component {
  constructor(){
    super();
    console.log("hello there i am constructor in the new.js");
    this.state = {
      articles: [],
      loading: false
    }
  }
  async componentDidMount(){
    console.log("dfdsfdas");
      let url =
        "https://newsapi.org/v2/top-headlines?country=in&apiKey=7b867eedef3745b5a6ecedc47276cfaf";   
      let data = await fetch(url);
      let parsedata = await data.json();
      console.log(parsedata);
      this.setState({articles: parsedata.articles});
  }

  render() {
    return (
      <>
        <div className="container my-3">
          <h1>News Headlines</h1>
          <div className="row">
            {this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsItem
                    title={element.title ? element.title.slice(0, 50) : ""}
                    description={
                      element.description
                        ? element.description.slice(0, 100)
                        : ""
                    }
                    imageUrl={
                      element.urlToImage
                        ? element.urlToImage
                        : "https://akm-img-a-in.tosshub.com/indiatoday/2023-01/mars%20sample%20return.gif?VersionId=Aav6viGUADS8lx8cuegKfG8ETRHTDrnv"
                    }
                    newsUrl={element.url}
                  />
                </div>
              );
            })}
          </div>
          <div className="conatiner d-flex justify-content-between">
            <button type="button" className="btn btn-dark">
              Previous
            </button>
            <button type="button" className="btn btn-dark ">
              Next
            </button>
          </div>
        </div>
      </>
    );
  }
}

export default News
