import React, { Component } from 'react'
import NewsItem from './NewsItem';
import PropTypes from 'prop-types'
import Spinner from './Spinner';
import InfiniteScroll from 'react-infinite-scroll-component';

export class News extends Component {
  static defaultProps = {
    country: "in",
    pageSize: "10",
    category: "science",
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults:0
    };
    
  }
  // async updateNews(){ 
  //   let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=7b867eedef3745b5a6ecedc47276cfaf&page=${this.state.page}&pagesize=${this.props.pageSize}`;
  //   this.setState({
  //     loading: true,
  //   });
  //   let data = await fetch(url);
  //   let parsedata = await data.json();
  //   this.setState({
  //     articles: parsedata.articles,
  //     loading: false,
  //     totalResults: parsedata.totalResults,
  //   });    
  //   document.title = `${this.props.category}-NewsApp`;

  // }
  async componentDidMount() {    
    this.updateNews();
  }
  // handlePrevious = async () => {
  //   this.setState({page: this.state.page-1});
  //   this.updateNews();
  // };
  // handleNext = async () => {
  //   this.setState({page: this.state.page+1});
  //   this.updateNews();
  // };
  fetchMoreData = async()=>{
    this.setState({page: this.state.page+1});
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=7b867eedef3745b5a6ecedc47276cfaf&page=${this.state.page}&pagesize=${this.props.pageSize}`;
    this.setState({
      loading: true,
    });
    let data = await fetch(url);
    let parsedata = await data.json();
    this.setState({
      articles: this.state.articles.concat(parsedata.articles),
      loading: false,
      totalResults: parsedata.totalResults,
    });    
    document.title = `${this.props.category}-NewsApp`;
  }


  render() {
    return (
      <>
        <div className="container my-3">
          <h1 className="text-center" >NewsApp - Top Headlines ({this.props.category})</h1>
          {/* {this.state.loading && <Spinner />} */}
          <InfiniteScroll
                dataLength={this.state.items.length}
                next={this.fetchMoreData}
                style={{ display: 'flex', flexDirection: 'column-reverse' }} 
                hasMore={this.state.articles.length!=this.state.totalResults}
                loader={<Spinner/>}
                // scrollableTarget="scrollableDiv"
                >
              <div className="row">              
                {this.state.articles.map((element) => {
                    return (
                      <di v className="col-md-4" key={element.url}>
                        <NewsItem  
                        title={element.title ? element.title.slice(0, 50) : ""}  description={
                            element.description ? element.description.slice(0, 100) : ""
                          }
                          imageUrl={
                            element.urlToImage
                              ? element.urlToImage
                              : "https://akm-img-a-in.tosshub.com/indiatoday/2023-01/mars%20sample%20return.gif?VersionId=Aav6viGUADS8lx8cuegKfG8ETRHTDrnv"
                          }
                          newsUrl={element.url}
                          author = {element.author}
                          date = {element.publishedAt}
                        />
                </di>
                );
              })}
            </div>
          </InfiniteScroll>
        </div>
      </>
    );
  }
}

export default News
